function b = setupKeys_catLoc(task)% keyboard set upKbName('UnifyKeyNames');b.resp = KbName([task.responseButtons task.quitKey]);b.quit = numel(b.resp);b.otherKeys = KbName({'c','v','d','a','r','t','escape','space','return','Return'});  %FlushEvents('keyDown');%Restrict keyboard so nothing works except necessary keysRestrictKeysForKbCheck([b.resp b.otherKeys]);% start GetChar listening% input argument 2  will suppress output of keypresses to the Matlab windowsif ~IsWin    ListenChar(2);else    KbQueueCreate;end